$(function () {
    $("#side-menu").metisMenu()
});
$(function () {
    $(window).bind("load resize", function () {
        topOffset = 50;
        width = 0 < this.window.innerWidth ? this.window.innerWidth : this.screen.width;
        768 > width ? ($("div.navbar-collapse").addClass("collapse"), topOffset = 100) : $("div.navbar-collapse").removeClass("collapse");
        height = (0 < this.window.innerHeight ? this.window.innerHeight : this.screen.height) - 1;
        height -= topOffset;
        1 > height && (height = 1);
        height > topOffset && $("#page-wrapper").css("min-height", height + "px")
    });
    var b = window.location, a = $("ul.nav a").filter(function () {
        return this.href ==
            b
    }).addClass("active").parent().parent().addClass("in").parent();
    a.is("li") && a.addClass("active");

});
function hideImage(b, a, c) {
    $(b).hide();
    $(a).hide("slow");
    $(c).val(!0)
};
